tinymce.PluginManager.add('versiculo', function(editor, url) {
  // Adds a menu item to the tools menu
  editor.addMenuItem('versiculo', {
    text: 'Agregar Versiculos',
    context: 'tools',
    onclick: function() {
      // Open window with a specific url
      editor.windowManager.open({
        title: 'Agregar Versiculos',
        url: 'http://torrefuerte.app/area-espiritual/buscar-versiculo',
        width: 600,
        height: 400,
        buttons: [
          {
            text: 'Agregar',
            onclick: function(){
              var activo = top.tinymce.activeEditor.dom;
              var ventana = activo.select("#prueba");

              alert(ventana.innerHTML);

              var cadena = '&nbsp;<b> Agrege un versiculo!</b>' + '&nbsp;';
              editor.insertContent(cadena);
              top.tinymce.activeEditor.windowManager.close();
            }
          },
          {
            text: 'Close',
            onclick: 'close'
          }
        ]
      });
    }
  });
});